import React, { useState, useEffect } from "react";
import axios from 'axios'


function App() {

  const [post, setPost] = useState([])
  //const [zip, setZip] = useState([])
  const [city, setCity] = useState([])
  //const [text, setText] = useState([])

  // call API
  const fetchPost = async () => {

    //const response = await axios(`http://ctp-zip-api.herokuapp.com/zip/${zip}`)
    const response = await axios(`http://ctp-zip-api.herokuapp.com/city/${city}`)
    // const response = await axios("http://ctp-zip-api.herokuapp.com/zip/10010")
    setPost(response.data)
    console.log(response.data)

  }

  const convertUpperCase = () => {
    let upperCase = city.toUpperCase()
    setCity(upperCase)
  }

  useEffect(() => {
    fetchPost()
  }, [])

  return (
    <div className="zip-search">
      <h1>Hello world</h1>
      <div>
        {/* <input type = "text" value = {zip} onChange = {e => setZip(e.target.value)}/> */}
        <input type = "text" value = {convertUpperCase} onChange = {e => setCity(e.target.value)}/>
        <button onClick={fetchPost}>Search new city</button>
      </div>
      <div>
        {post.map(element => {
        return(
            <div className = "City" key={element}>
            <div><br></br></div>
            <div>{element}</div>
            {/* <div>{element.City}</div>
            <div>State: {element.State}</div>
            <div>Location: {element.Location}</div>
            <div>Population: {element.EstimatedPopulation}</div>
            <div>Total Wages: {element.TotalWages}</div> */}
        </div>

        )})}</div>
    </div>
  );

